import React, { useState } from "react";
import Fetch from "./Api";

const Username = () => {
  const [username, setUsername] = useState("");
  return (
    <div>
      <h1>Username</h1>
      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <Fetch username={username} />
    </div>
  );
};

export default Username;
