import React, { useState, useEffect } from "react";
import Fetch from "./Api";

const Username = () => {
  const [username, setUsername] = useState("EhsanShakil");

  useEffect((username) => {
    let url = "https://api.github.com/users";
    let changeableurl = `${url}/${username}`;
    fetch(changeableurl).then((data) => {
      console.log(data);
    });
  }, []);
  return (
    <div>
      <h1>Username</h1>

      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      {console.log(username)}
    </div>
  );
};

export default Username;
